package statements;

public class SwitchDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		String text = "sandy";
		
		switch(text) {
		
		case "ayush":	
			System.out.println("ayush");
			break;
			
		case "sandy":	
			System.out.println("sandy");
			break;
			
		case "surja":	
			System.out.println("surja");
			break;
			
		case "suyog":	
			System.out.println("suyog");
			break;
		
		}
		
		System.out.println("Program closed\n");
	}

}